# Sessions database setup
def get_sessions_db_connection():
    if not os.path.exists("sessions.db"):
        conn = sqlite3.connect("sessions.db")
        cursor = conn.cursor()
        cursor.execute(
            """
        CREATE TABLE sessions (
            id TEXT PRIMARY KEY,
            questions_asked INTEGER DEFAULT 0,
            last_question_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
        )
        """
        )
        conn.commit()
        conn.close()

    return sqlite3.connect("sessions.db")


# Setup conversations database
def get_conversations_db_connection():
    if not os.path.exists("conversations.db"):
        conn = sqlite3.connect("conversations.db")
        cursor = conn.cursor()

        # Create conversations table
        cursor.execute(
            """
            CREATE TABLE conversations (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                user_id INTEGER,
                title TEXT NOT NULL,
                created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                FOREIGN KEY (user_id) REFERENCES users(id)
            )
            """
        )

        # Create messages table
        cursor.execute(
            """
            CREATE TABLE messages (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                conversation_id INTEGER NOT NULL,
                sender TEXT NOT NULL,
                content TEXT NOT NULL,
                created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                FOREIGN KEY (conversation_id) REFERENCES conversations(id)
            )
            """
        )

        conn.commit()
        conn.close()

    return sqlite3.connect("conversations.db")


# Rate limiting functions
def check_rate_limit(session_id):
    """Check if the session has exceeded the question limit"""
    conn = get_sessions_db_connection()
    cursor = conn.cursor()

    try:
        cursor.execute(
            "SELECT questions_asked FROM sessions WHERE id = ?", (session_id,)
        )
        result = cursor.fetchone()

        if not result:
            cursor.execute(
                "INSERT INTO sessions (id, questions_asked) VALUES (?, 0)",
                (session_id,),
            )
            conn.commit()
            return True

        questions_asked = result[0]

        if questions_asked >= 3:
            return False

        cursor.execute(
            "UPDATE sessions SET questions_asked = questions_asked + 1 WHERE id = ?",
            (session_id,),
        )
        conn.commit()
        return True

    finally:
        conn.close()


def reset_rate_limit(session_id):
    """Reset the question limit for a session"""
    conn = get_sessions_db_connection()
    cursor = conn.cursor()
    try:
        cursor.execute(
            "UPDATE sessions SET questions_asked = 0 WHERE id = ?", (session_id,)
        )
        conn.commit()
    finally:
        conn.close()


def find_best_match(prompt, threshold=75):

    clean_prompt = re.sub(r"[^\w\s؟?.،,:;!]", "", prompt.lower().strip())

    matches = []

    basic_match, basic_score = process.extractOne(
        clean_prompt, [q[0].lower() for q in PREDEFINED_QA], scorer=fuzz.ratio
    )
    matches.append((basic_match, basic_score, "ratio"))

    token_match, token_score = process.extractOne(
        clean_prompt,
        [q[0].lower() for q in PREDEFINED_QA],
        scorer=fuzz.token_sort_ratio,
    )
    matches.append((token_match, token_score, "token_sort"))

    set_match, set_score = process.extractOne(
        clean_prompt, [q[0].lower() for q in PREDEFINED_QA], scorer=fuzz.token_set_ratio
    )
    matches.append((token_match, set_score, "token_set"))

    partial_match, partial_score = process.extractOne(
        clean_prompt, [q[0].lower() for q in PREDEFINED_QA], scorer=fuzz.partial_ratio
    )
    matches.append((partial_match, partial_score, "partial"))

    best_match_info = max(matches, key=lambda x: x[1])
    best_match, best_score, algorithm = best_match_info

    if best_score >= threshold:
        # Find the index of the matching question
        for i, (question, answer) in enumerate(PREDEFINED_QA):
            if question.lower() == best_match:
                return answer, best_score, algorithm

    return None, best_score, algorithm


def get_gemini_response(prompt):
    try:
        if not GEMINI_API_KEY:
            print("Gemini API key not found")
            return None

        # Try to find a match in predefined Q&A using enhanced fuzzy matching
        match_result, score, algorithm = find_best_match(prompt)

        if match_result:
            print(f"Found match with score {score} using {algorithm} algorithm")
            return match_result

        # If no good match is found, fall back to the Gemini API
        # Create a more structured context for the model
        context = (
            "You are a helpful assistant for the Faculty of Arts at Tanta University. "
        )
        context += "Your focus is on answering questions about the Information Technology and Libraries department. "
        context += "If you don't know the answer, suggest the student visit the department's website or Facebook page. "
        context += "Keep answers concise and relevant to academic matters."

        # Prepare the prompt with context and examples
        full_prompt = f"{context}\n\nUser Question: {prompt}\n\n"
        full_prompt += "Here are some examples of questions and their answers to help you understand the context:\n\n"

        # Add a subset of examples for context (not all to avoid token limits)
        key_examples = PREDEFINED_QA[:5]  # Using first 5 examples for context
        for question, answer in key_examples:
            full_prompt += f"Q: {question}\nA: {answer}\n\n"

        url = "https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent"
        headers = {
            "Content-Type": "application/json",
        }
        params = {"key": GEMINI_API_KEY}
        data = {
            "contents": [{"parts": [{"text": full_prompt}]}],
            "generationConfig": {
                "temperature": 0.7,
                "topK": 40,
                "topP": 0.95,
                "maxOutputTokens": 1024,
            },
        }

        response = requests.post(url, headers=headers, params=params, json=data)

        if response.status_code == 200:
            result = response.json()
            if "candidates" in result and len(result["candidates"]) > 0:
                return result["candidates"][0]["content"]["parts"][0]["text"]
            else:
                print("No candidates in Gemini response")
                return None
        else:
            print(f"Gemini API error: {response.status_code}, {response.text}")
            return None
    except Exception as e:
        print(f"Error calling Gemini API: {e}")
        return None


def get_user_id_from_token(auth_header):
    """Extract user ID from the authorization header"""
    if not auth_header or not auth_header.startswith("Bearer "):
        return None

    token = auth_header.split(" ")[1]
    try:
        payload = jwt.decode(token, JWT_SECRET, algorithms=["HS256"])
        return payload.get("user_id")
    except jwt.ExpiredSignatureError:
        return None
    except jwt.InvalidTokenError:
        return None


@app.route("/api/signup", methods=["POST"])
def signup():
    data = request.json
    username = data.get("username")
    email = data.get("email")
    password = data.get("password")

    hashed_password = bcrypt.hashpw(password.encode("utf-8"), bcrypt.gensalt())

    conn = get_db_connection()
    cursor = conn.cursor()

    try:
        cursor.execute(
            "SELECT * FROM users WHERE username = ? OR email = ?", (username, email)
        )
        existing_user = cursor.fetchone()

        if existing_user:
            return jsonify({"error": "Username or email already exists"}), 400

        cursor.execute(
            "INSERT INTO users (username, email, password) VALUES (?, ?, ?)",
            (username, email, hashed_password),
        )
        conn.commit()

        return jsonify({"message": "User created successfully"})
    except Exception as e:
        conn.rollback()
        return jsonify({"error": str(e)}), 500
    finally:
        conn.close()


@app.route("/api/login", methods=["POST"])
def login():
    data = request.json
    email = data.get("email")
    password = data.get("password")

    conn = get_db_connection()
    cursor = conn.cursor()

    try:
        cursor.execute("SELECT * FROM users WHERE email = ?", (email,))
        user = cursor.fetchone()

        if not user or not bcrypt.checkpw(password.encode("utf-8"), user[3]):
            return jsonify({"error": "Invalid email or password"}), 401

        token = jwt.encode(
            {
                "user_id": user[0],
                "username": user[1],
                "email": user[2],
                "exp": datetime.now(UTC) + timedelta(days=1),
            },
            JWT_SECRET,
            algorithm="HS256",
        )

        session_id = request.headers.get("Session-ID")
        if session_id:
            reset_rate_limit(session_id)

        return jsonify(
            {
                "token": token,
                "user": {"id": user[0], "username": user[1], "email": user[2]},
            }
        )
    except Exception as e:
        return jsonify({"error": str(e)}), 500
    finally:
        conn.close()


