EMAIL_ADDRESS = os.getenv("EMAIL_ADDRESS")
EMAIL_PASSWORD = os.getenv("EMAIL_PASSWORD")


def send_email(to_email, subject, html_content):
    try:
        msg = MIMEMultipart()
        msg["From"] = EMAIL_ADDRESS
        msg["To"] = to_email
        msg["Subject"] = subject

        msg.attach(MIMEText(html_content, "html"))

        server = smtplib.SMTP("smtp.gmail.com", 587)
        server.starttls()
        server.login(EMAIL_ADDRESS, EMAIL_PASSWORD)
        server.send_message(msg)
        server.quit()

        print(f"[DEBUG] Email sent to {to_email} with subject '{subject}'")
        return True
    except Exception as e:
        print(f"[DEBUG] Email sending failed: {e}")
        return False


reset_codes = {}


# Generate a 6-character alphanumeric reset code
def generate_reset_code():
    import random
    import string

    code = "".join(random.choices(string.ascii_uppercase + string.digits, k=6))
    print(f"[DEBUG] Generated reset code: {code}")  # Debug output to terminal
    return code


@app.route("/api/reset-password", methods=["POST", "OPTIONS"])
def reset_password():
    if request.method == "OPTIONS":
        return jsonify({}), 200  # CORS preflight response

    data = request.json
    token = data.get("token")
    new_password = data.get("new_password")

    conn = sqlite3.connect("users.db")
    cursor = conn.cursor()
    cursor.execute("SELECT * FROM users WHERE reset_token = ?", (token,))
    user = cursor.fetchone()

    if user:
        hashed_password = bcrypt.hashpw(new_password.encode("utf-8"), bcrypt.gensalt())
        cursor.execute(
            "UPDATE users SET password = ?, reset_token = NULL WHERE reset_token = ?",
            (hashed_password, token),
        )
        conn.commit()
        conn.close()
        print(f"[DEBUG] Password reset successful for token: {token}")
        return jsonify({"message": "Password reset successful"})
    else:
        conn.close()
        print(f"[DEBUG] Invalid or expired token: {token}")
        return jsonify({"message": "Invalid or expired token"}), 400


